import React from 'react'
import ProductsContainer from './ProductsContainer'
import LoginComponent from './Login'
import { connect } from 'react-redux'
import { login } from '../actions/user'


class LoginGate extends React.Component {

    conditionalRender = () => {

        if (this.props.user.status === "resolved"){
            <div>
                <ProductsContainer />
            </div>
        }else if (this.props.user.status === "idle"){
            <div>
                <LoginComponent />
            </div>
        }else{
            <div>
                Loading...
            </div>
        }
    }

    render() {
        console.log('props from LoginGate', this.props)
        return(
            <div>
                {this.conditionalRender()}
            </div>
        )
    }

}


const mapStateToProps = state => {
    return {
        status: state.user.status
    }
}

export default connect(mapStateToProps, {login})(LoginGate)
