import React, { component } from 'react';

export default class PopUp extends Component {

    handleClick = () => {
        this.props.toggle();
    };

    render() {
        return (
            <div className="modal">
                <div className="modal_content">
                    <span className="close" onClick={this.handleClick}>&times;
                    </span>
                    <p>Hello this is the pop up!</p>
                </div>
            </div>
        );
    }

}
