import React from 'react'
import { connect } from 'react-redux'
import {fetchProducts} from '../actions/fetchProducts'
import ProductsContainer from './ProductsContainer'
import LoginComponent from './Login'


class LoginGate extends React.Component {

    conditionalRender = () => {
        if (this.props.status === "resolved"){
            <div>
                <ProductsContainer />
            </div>
        }else{
            <div>
                <LoginComponent />
            </div>
        }
    }

    render() {
        return(
            <div>
                {this.conditionalRender()}
            </div>
        )
    }

}


const mapStateToProps = state => {
    console.log("state from LoginGate", state)

    return {
        products: state.productReducer.products.data,
        status: state.user.status,
    }
}

export default connect(mapStateToProps, {fetchProducts})(ProductsContainer)
