import React, { Component } from 'react'
import Button from './Button.js'

// import { Card, CardMedia, CardContent, CardActions, Typography, IconButton} from '@material-ui/core';
// import { AddShoppingCart } from '@material-ui/icons'
// import classes  from '*.module.css';

class Product extends Component { //destructure product from the props

    handleClick = (e) => {
        e.preventDefault()
        console.log("you clicked the button")
    }


    render(){
        const productCardStyle = {
            border: `2px solid grey`,
            boxShadow: `3px 4px  rgb(39, 209, 135)`
        }

        return (
            <div className="product-card" style={productCardStyle}>
                <p>"I'm a product!"</p>
                <Button handleClick={this.handleClick} label="Add to cart"></Button>
            </div>
        )
    }
} 

export default Product
